              <SelectContent>
                <SelectItem value="none">Kein Workout</SelectItem>
                {recentWorkouts.map(w => (
                  <SelectItem key={w.record_id} value={w.record_id}>
                    {w.fields.datum?.split('T')[0] || '?'} - {WORKOUT_TYP_LABELS[w.fields.typ || ''] || w.fields.typ}
