        open={workoutLogDialog}
        onOpenChange={setWorkoutLogDialog}
        record={editWorkoutLog}
        workouts={workouts}
        uebungen={uebungen}
        onSuccess={fetchAll}
      />

      {/* ÜBUNG DIALOG */}
      <UebungFormDialog
        open={uebungDialog}
        onOpenChange={setUebungDialog}
        record={editUebung}
        onSuccess={fetchAll}
      />

      {/* ZIELE DIALOG */}
      <ZieleFormDialog
        open={zieleDialog}
        onOpenChange={setZieleDialog}
        record={editZiel}
        onSuccess={fetchAll}
      />

      {/* KÖRPERDATEN DIALOG */}
      <KoerperdatenFormDialog
        open={koerperdatenDialog}
        onOpenChange={setKoerperdatenDialog}
        record={editKoerperdaten}
        onSuccess={fetchAll}
      />

      {/* DELETE CONFIRMATION */}
      <DeleteConfirm
        open={!!deleteTarget}
        onOpenChange={(o) => { if (!o) setDeleteTarget(null); }}
        name={deleteTarget?.name || ''}
        onConfirm={handleDelete}
        deleting={deleting}
      />
    </div>
  );
}

