   359→
   360→      {/* ---- Top Row ---- */}
   361→      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3">
   362→
   363→        {/* Today's Workout */}
   364→        <div className="col-span-2 rounded-2xl border border-border bg-card p-5 space-y-3 shadow-sm">
