                : (editingRecord ? 'Speichern' : 'Erstellen')}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete Confirm Dialog */}
      <DeleteConfirmDialog
        open={deleteDialogOpen}
        onOpenChange={setDeleteDialogOpen}
        recordName={deletingRecord ? getRecordName(deletingRecord) : ''}
        onConfirm={handleDelete}
      />
    </div>
  )
}
