    34→        r={radius}
    35→        fill="none"
    36→        stroke={color}
    37→        strokeWidth={strokeWidth}
    38→        strokeLinecap="round"
    39→        strokeDasharray={circumference}
    40→        strokeDashoffset={dashoffset}
    41→        className="transition-all duration-700 ease-out"
