const RPE_LABELS: Record<string, string> = {
  rpe_1: '1 - Sehr leicht',
  rpe_2: '2',
  rpe_3: '3',
  rpe_4: '4',
  rpe_5: '5 - Mittel',
  rpe_6: '6',
  rpe_7: '7',
