    13→function ProgressRing({ value, max, size = 120, strokeWidth = 8, color = 'hsl(174 62% 38%)' }: {
    14→  value: number; max: number; size?: number; strokeWidth?: number; color?: string
    15→}) {
    16→  const radius = (size - strokeWidth) / 2
    17→  const circumference = 2 * Math.PI * radius
    18→  const progress = max > 0 ? Math.min(value / max, 1) : 0
    19→  const dashoffset = circumference * (1 - progress)
    20→
    21→  return (
    22→    <svg width={size} height={size} className="transform -rotate-90">
    23→      <circle
    24→        cx={size / 2}
    25→        cy={size / 2}
    26→        r={radius}
    27→        fill="none"
    28→        stroke="hsl(35 20% 95%)"
    29→        strokeWidth={strokeWidth}
    30→      />
    31→      <circle
    32→        cx={size / 2}
    33→        cy={size / 2}
    34→        r={radius}
    35→        fill="none"
    36→        stroke={color}
    37→        strokeWidth={strokeWidth}
    38→        strokeLinecap="round"
    39→        strokeDasharray={circumference}
    40→        strokeDashoffset={dashoffset}
    41→        className="transition-all duration-700 ease-out"
    42→      />
    43→    </svg>
    44→  )
    45→}
