  rpe_2: '2',
  rpe_3: '3',
  rpe_4: '4',
  rpe_5: '5 - Mittel',
  rpe_6: '6',
  rpe_7: '7',
  rpe_8: '8',
  rpe_9: '9',
  rpe_10: '10 - Maximal',
}

const RPE_VALUES = [
  'rpe_1', 'rpe_2', 'rpe_3', 'rpe_4', 'rpe_5',
  'rpe_6', 'rpe_7', 'rpe_8', 'rpe_9', 'rpe_10',
] as const

const WORKOUT_TYP_LABELS: Record<string, string> = {
  push: 'Push',
  pull: 'Pull',
  beine: 'Beine',
  ganzkoerper: 'Ganzkörper',
  oberkoerper: 'Oberkörper',
  unterkoerper: 'Unterkörper',
  cardio: 'Cardio',
  sonstiges: 'Sonstiges',
}

