// --- Label Lookup Maps ---

const muskelgruppeLabels: Record<NonNullable<Uebungen['fields']['muskelgruppe']>, string> = {
  brust: 'Brust',
  ruecken: 'Rücken',
  beine: 'Beine',
  schultern: 'Schultern',
  bizeps: 'Bizeps',
  trizeps: 'Trizeps',
